import express from "express";
import routes from "../routes";

import {
    users,
    getEditProfile, postEditProfile,
    userDetail,
    changePassword
} from "../controllers/userController";

const userRouter = express.Router();

userRouter.get(routes.users, users);

// /user/:id/changePassword
